import { Component, inject, OnInit } from '@angular/core';
import { CitasServicioService } from '../Services/citas-servicio.service';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import { EmpresaService } from 'src/app/admin/services/empresa/empresa.service';
import { Empresa } from 'src/app/models/Empresa';
import { QRCodeComponent } from 'angularx-qrcode';
import * as QRCode from 'qrcode';
@Component({
  selector: 'app-facturas-comprobantes',
  templateUrl: './facturas-comprobantes.component.html',
  styleUrls: ['./facturas-comprobantes.component.css'],
})
export class FacturasComprobantesComponent implements OnInit {
  //servicios
  citasServicio = inject(CitasServicioService);
  empresaServicio = inject(EmpresaService);
  todasCitas: any;
  valorEmpresa!: Empresa;

  ngOnInit(): void {
    this.empresaServicio.obtenerInfoEmpresa().subscribe((empresas: any) => {
      console.log(empresas);

      this.valorEmpresa = empresas.empresa;
    });
    this.citasServicio.obtenerCitasId().subscribe((citas: any) => {
      this.todasCitas = citas.todoServiciosEspecificos;
      citas.todoServiciosEspecificos.forEach((element: any) => {
        console.log(element);
      });
    });
  }
  //******** */ Paginaci√≥n
  currentPage = 1;
  itemsPerPage = 5;

  get totalPages(): number {
    return Math.ceil(this.todasCitas.length / this.itemsPerPage);
  }

  get paginatedItems() {
    const start = (this.currentPage - 1) * this.itemsPerPage;
    return this.todasCitas.slice(start, start + this.itemsPerPage);
  }

  nextPage() {
    if (this.currentPage < this.totalPages) {
      this.currentPage++;
    }
  }

  prevPage() {
    if (this.currentPage > 1) {
      this.currentPage--;
    }
  }

  //formato
  formatDate(date: Date): string {
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0'); // Los meses son indexados desde 0
    const year = date.getFullYear();
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    const seconds = String(date.getSeconds()).padStart(2, '0');

    return `${day}/${month}/${year} ${hours}:${minutes}:${seconds}`;
  }
  //Generador de pdf
  toBase64(url: string): Promise<string> {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.crossOrigin = 'Anonymous'; // Para evitar problemas de CORS
      img.onload = () => {
        const canvas = document.createElement('canvas');
        canvas.width = img.width;
        canvas.height = img.height;
        const ctx = canvas.getContext('2d');
        ctx?.drawImage(img, 0, 0);
        resolve(canvas.toDataURL('image/png'));
      };
      img.onerror = (error) => reject(error);
      img.src = url;
    });
  }
  async generatePDF(citaEspecifica: any) {
    const doc = new jsPDF();
    const today = new Date();
    const formattedDate = this.formatDate(today);

    try {
      const imgData =
        '';
      doc.setFontSize(16);
      doc.text(this.valorEmpresa.nombre, 10, 20);
      doc.text(formattedDate, 10, 30);
      doc.text('Factura #' + citaEspecifica.id, 10, 40);
      doc.addImage(imgData, 'JPEG', 10, 50, 50, 50);
      doc.setFontSize(12);
      doc.text('Este es el recuento de servicios de su cita', 10, 50);
      const datosJson = {
        ID: citaEspecifica.id,
        Usuario: citaEspecifica.idUsuario.nombreUsuario,
        Correo: citaEspecifica.idUsuario.persona.correo,
        Telefono: citaEspecifica.idUsuario.persona.telefono,
        Servicio: citaEspecifica.idServicio.nombre,
        EstadoCita: citaEspecifica.idEstadoCita.nombre,
        Hora_incio: this.formatDate(new Date(citaEspecifica.fechaHoraInicio)),
        Hora_fin: this.formatDate(new Date(citaEspecifica.fechaHoraFin)),
        D√≠a: citaEspecifica.idDiaLaboral.nombre,
      };
      const jsonData = JSON.stringify(datosJson);

      const qrCodeData = await QRCode.toDataURL(jsonData);

      doc.addImage(qrCodeData, 'PNG', 150, 20, 50, 55);
      const headers = [
        [
          'ID',
          'Usuario',
          'Correo',
          'Tel√©fono',
          'Servicio',
          'Estado de Cita',
          'Fecha Hora Inicio',
          'Fecha Hora Fin',
          'D√≠a Laboral',
        ],
      ];
      const data = [
        [
          citaEspecifica.id,
          citaEspecifica.idUsuario.nombreUsuario,
          citaEspecifica.idUsuario.persona.correo,
          citaEspecifica.idUsuario.persona.telefono,
          citaEspecifica.idServicio.nombre,
          citaEspecifica.idEstadoCita.nombre,
          this.formatDate(new Date(citaEspecifica.fechaHoraInicio)),
          this.formatDate(new Date(citaEspecifica.fechaHoraFin)),
          citaEspecifica.idDiaLaboral.nombre,
        ],
      ];

      autoTable(doc, {
        head: headers,
        body: data,
        startY: 130,
      });

      doc.save('Cita' + citaEspecifica.id + '.pdf');
    } catch (error) {
      console.error('Error loading image:', error, this.valorEmpresa?.logo);
    }
  }
}
